/**
 * Trigger AI Agent automation by sending order data to webhook
 * @param {Object} orderData - Complete order information
 * @returns {Promise<Object>} - Response from webhook
 */
async function triggerAutomation(orderData) {
  const webhookUrl = process.env.WEBHOOK_URL;
  
  if (!webhookUrl) {
    console.warn('WEBHOOK_URL not configured. Skipping automation trigger.');
    return null;
  }

  try {
    // Generate WhatsApp message and email template
    const whatsAppMessage = generateWhatsAppMessage(orderData);
    const whatsAppPrompt = generateWhatsAppPrompt(orderData);
    const emailTemplate = generateEmailTemplate(orderData);

    // Prepare payload for webhook
    const payload = {
      orderId: orderData.orderId,
      customerName: orderData.customerName,
      customerEmail: orderData.customerEmail,
      customerPhone: orderData.customerPhone,
      products: orderData.products,
      totalPrice: orderData.totalPrice,
      shippingAddress: orderData.shippingAddress,
      whatsAppMessage: whatsAppMessage,
      whatsAppPrompt: whatsAppPrompt,
      emailSubject: emailTemplate.subject,
      emailBody: emailTemplate.body,
      timestamp: new Date().toISOString()
    };

    // Send to webhook (n8n or other automation service)
    const response = await axios.post(webhookUrl, payload, {
      headers: {
        'Content-Type': 'application/json'
      },
      timeout: 10000 // 10 seconds timeout
    });

    console.log('AI Agent automation triggered successfully:', response.status);
    return response.data;
  } catch (error) {
    console.error('Error triggering AI Agent automation:', error.message);
    // Don't throw error - automation failure shouldn't break order creation
    return null;
  }
}

module.exports = {
  generateWhatsAppPrompt,
  generateWhatsAppMessage,
  generateEmailTemplate,
  triggerAutomation
};
